/* FOOTER-Popup Form */
.dt-popup-full .popup-close {
    color: #333 !important;
}
.popup-request-form h2 {
    text-align: center;
    color: #fff;
    font-family: Playfair Display, sans-serif;
    font-weight: 400;
    font-style: italic;
    text-transform: none;
    margin-bottom: 40px;
}
.popup-request-form {
  background :#303030;
  padding:40px;
  position:relative;
}

.popup-request-form input[type=text],
.popup-request-form input[type=email]{
  background:transparent;
  border:1px solid #5d5d5d;
  padding:7px;
  color:#fff;
  width: 100%;
  margin-bottom: 10px;
}

.popup-request-form span textarea {
    background-color: transparent;
    border:1px solid #5d5d5d;
    color: #fff;
    width: 100%;
    margin-bottom: 20px;
    padding: 10px 20px;
}

.popup-request-form [type=submit] {
  border: 5px solid #FFDA4F;
  background-color:#FC6D53;
  font-family: open sans;
  font-weight: 700;
  padding:5px 30px;
  position:absolute;
  bottom:-20px;
  left:50%;
  -webkit-transform :translateX(-50%);
  -moz-transform :translateX(-50%);
  -transform :translateX(-50%);
  color:#fff;
  text-transform:uppercase;
}

div.wpcf7-validation-errors,
div.wpcf7-mail-sent-ok{
  border:none;
  background-color: #111;
  margin: 40px -15px;;
  padding: 20px 60px;
}
div.wpcf7-validation-errors {
    color: #F00
}
div.wpcf7-mail-sent-ok {
    color:#fff;
}

/* FOOTER-Fullscreen Popup */
.fullscreen-popup-container {
  max-width:980px;
  margin:0 auto;
  float:none;
}
.dt-popup-full .popup-close {
  position:absolute;
  top:10px;
  right:20px;
  color:#fff;
}
.height-100-vh {
    height:100vh;
    display:table;
    width:100%;
    margin-left: 0;
    margin-right: 0;
}
.fullscreen-popup-container {
    display:table-cell;
    vertical-align:middle;
}

.fullscreen-popup-container > div {
    max-width:980px;
    margin:0 auto;
}
/* Contact Form */
.hue-contact .wpcf7-text, .hue-contact .wpcf7-textarea {
    width: 100%;
