css-code:
                body{
height: 100%;
}
.pre-footer {
    border-top: 12px solid #5B820A;
}
.pushmenuoverlay nav > ul > li > a > span:hover, .pushmenuoverlay .dropmenu a:hover {
    color: #618713!important;
}
/* Banner */
section#banner-section .page-title, section#banner-section .breadcrumbs, section#banner-section .breadcrumbs a {
    color: #ffffff;
}
section#banner-section .breadcrumbs .current {
    color: #fff;
}
/* footer social */
#menu-footer-social {
    margin: 0;
    padding: 0;
}
#menu-footer-social li {
    list-style: none;
    float: left;
    margin-right: 8px;
}
#menu-footer-social li a {
    width: 35px;
    height: 35px;
    background: #fff;
    color: #0D1B06;
    display: block;
    border-radius: 50%;
    text-align: center;
    padding-top: 5px;
}
.widgettitle{
	font-size: 16px; 
	color: #fff;
}
#menu-footer-pages li,
#menu-footer-partners li{
	list-style: none;
    border-bottom: 0;
    border-top: 0;
}
.fside {
    padding-left: 90px;
    padding-right: 45px;
}
.fside.bordered {
    border-right: 1px solid #3E4739;
}
.dt-counto {
    font-size: 32px;
    font-weight: 700;
    color: #303030;
    margin-bottom: 10px;
}
.lr-side{
	padding-right: 100px;
	padding-left: 100px;
}    

.lr-side-2{
	padding-right: 5%;
	padding-left: 5%;
} 

.l-side{
	padding-left: 5%;
} 

.product-description > div{
	margin-left: 40px;
}



/* VC ACCORDION */
.vc_tta-color-grey.vc_tta-style-classic .vc_tta-panel.vc_active .vc_tta-panel-heading {
    border-color: #979797 !important;
    background-color: #ffffff !important;
}
.vc_tta-color-grey.vc_tta-style-classic .vc_tta-panel .vc_tta-panel-heading {
    border-color: #979797 !important;
    background-color: #ffffff !important;
}
.vc_tta-color-grey.vc_tta-style-classic .vc_tta-panel .vc_tta-panel-body {
    background-color: #ffffff !important;
}
.vc_tta-color-grey.vc_tta-style-classic .vc_tta-panel .vc_tta-panel-body, .vc_tta-color-grey.vc_tta-style-classic .vc_tta-panel .vc_tta-panel-body::after, .vc_tta-color-grey.vc_tta-style-classic .vc_tta-panel .vc_tta-panel-body::before {
    border-color: #979797 !important;
}
.vc_tta.vc_tta-shape-rounded .vc_tta-panel-body, .vc_tta.vc_tta-shape-rounded .vc_tta-panel-heading, .vc_tta.vc_tta-shape-rounded .vc_tta-tab>a {
    border-radius: 3px !important;
}
.vc_tta.vc_tta-shape-rounded.vc_tta-o-shape-group.vc_tta-o-no-fill .vc_tta-panel.vc_active+.vc_tta-panel:last-child:not(:first-child) .vc_tta-panel-heading, .vc_tta.vc_tta-shape-rounded.vc_tta-o-shape-group.vc_tta-o-no-fill .vc_tta-panel.vc_active+.vc_tta-panel:not(:first-child):not(:last-child) .vc_tta-panel-heading {
    border-top-left-radius: 3px !important;
    border-top-right-radius: 3px !important;
}

/* Testimony */
.testimonial-layout-4{
	margin-bottom: 40px;
}
.testimonial-layout-4 .people-text{
	color: #303030;
}

/* Partner */
.partner-wrap {
    text-align: center;
}
.partner-wrap img {
    margin-left: 70px;
    margin-right: 70px;
    margin-top: 30px;
    margin-bottom: 30px;
}

@media(min-width:1023px) {
  .expand-50-percent-fullwidth {
    width:calc(50vw - 36px);
  }
  .expand-50-percent-fullwidth_lefty {
    margin-left: calc(-50vw + 100%);
  }
  .google-map{
      margin-left: calc(-50vw + 100%);
  }
  
}
.widget_tag_cloud .tagcloud .tag a {
    background-color: #5b820a;
}

.margin-bottom-25 {
    margin-bottom: 25px;
}

/* Contact Form */
.hue-contact .wpcf7-text, .hue-contact .wpcf7-textarea {
    width: 100%;
    border: 1px solid #979797;
    background: transparent;
    padding: 10px;
}
.btn-round{
    border-radius: 30px;
    padding: 10px 30px;
    color: #fff;
	background-color: #5b820a;
}
.btn-round:hover{
    background-color: #0d1b06;
}
@media (max-width: 425px){
    .dt-section-head .section-main-title {
        font-size: 25px !important;
    }
}
@media (max-width: 991px){
	.dt-boxed-stretched-container #main-nav-check ~ .top-head .adminbar-not-here .is-fixed #dt-menu-mobile {
	    margin-left: 0 !important;
	}
}
@media (max-width: 1024px) {
	.lr-side, .lr-side-2{
		padding-right: 0;
		padding-left: 0;
	} 
	.l-side{
	   padding-left: 0;
    } 
	.fside {
	    padding-left: 0px;
	    padding-right: 10px;
	}
	.partner-wrap img {
	    margin-left: 10px;
	    margin-right: 10px;
	}
    .product-description > div{
    	margin-left: 0;
    }
    .partner-wrap img {
        margin-left: 20px;
        margin-right: 20px;
    }
}
