css-code:
body{
height: 100%;
}

/* Menu */
.is-sticky #dt-menu li a {
	color: #565a5c;
}
.home .is-sticky #dt-menu li a,
#dt-menu li a {
  color: #ffffff;
}
#dt-menu #menu-main-menu .current-menu-item > a{
  color: #90d5e5;
}

/* BTN */
.btn-resort button, .btn-resort a{
    padding: 8px 28px !important;
    font-size: 20px !important;
    border-radius: 10px !important;
    border-width: 3px !important;
    border-color: #60C5E6 !important;
    color: #8C8C8C !important;
    background-color: transparent !important;
}
.btn-resort button:hover, .btn-resort a:hover{
    color: #ffffff !important;
    background-color: #60C5E6 !important;
}

.pos-relative-atas{position: relative; z-index: 2;}
.titlepage{font-size: 45px;color: #303030;}
.bgwhite{padding: 50px 60px; background-color: #fff;}
.w80{width: 80%; margin: 0 auto;}


.wrap-testimony .owl-carousel-container {
    background-color: #fff;
    width: 40%;
    padding: 50px 40px;
    float: right;
    margin-right: 10%;
    margin-bottom: -40px;
    z-index: 2;
    position: relative;
}
.lr-side{
	padding-right: 100px;
	padding-left: 100px;
}    

.lr-side-2{
	padding-right: 5%;
	padding-left: 5%;
} 
.right-padding{
    padding-right: 50px;
}
/* Accordion */
.vc_tta-color-grey.vc_tta-style-classic .vc_tta-panel .vc_tta-panel-heading {
    background-color: #ffffff !important;
}

@media (max-width: 1024px) {
	.lr-side, .lr-side-2{
		padding-right: 0;
		padding-left: 0;
	} 
	.right-padding{
        padding-right: 0;
    }
	[id^="section-"].dt-section-head .section-main-title {
	    font-size: 30px !important;
	}
}
@media(max-width:425px) {
	.wrap-testimony .owl-carousel-container {
	    width: 300px;
	    margin-right: 0%;
	    margin-bottom: 0;
	}
}
@media(max-width:320px) {
	.wrap-testimony .owl-carousel-container {
	    width: 270px;
	}
}


/* Form */
.footer .wpcf7{ float: right;}
.footer .wpcf7 label{font-weight: normal;}
.footer .wpcf7 .your-email{
    margin-top: 15px;
    display: block;
}
.footer .wpcf7 input[type=email]{
	background-color: transparent;
    border-color: transparent;
    border-bottom-color: #515151;
    border-width: 1px;
    padding: 10px 0;
}
.footer .wpcf7 input[type=submit]{
    position: absolute;
    bottom: 20px;
    right: 15px;
    visibility: hidden;
}
.footer .wpcf7 .icones{
    position: absolute;
    bottom: 30px;
    right: 20px;
} 

.margin-bottom-20 {
    margin-bottom: 20px;
}
.margin-bottom-25 {
    margin-bottom: 25px;
}

/* Contact Form */
.hue-contact .wpcf7-text, .hue-contact .wpcf7-textarea {
    width: 100%;
    border: 1px solid #e6e6e6;
    background: #ffffff;
    padding: 10px;
}
.btn-round{
    padding: 10px 30px;
    color: #fff;
	background-color: #60c5e6;
    border-radius: 10px;
}
.btn-round:hover{
    background-color: #60E6C0;
}
